import React, { Component } from 'react';
import 'font-awesome/css/font-awesome.min.css';
import { withRouter } from 'react-router-dom';
class SearchBox extends Component {
    state = {
        query:null
    }
    onSubmitHandler = (e) =>{
        console.log('go to submit handler');
        this.props.history.push({
            pathname: '/search',
            state: { query: this.state.query }
          }) 
    }
    handleChange = (e) => {
        this.setState({
            query:e.target.value
        });
     }
    render() { 
        return (  
            <div className="d-block" onSubmit>
                <form className="form-inline justify-content-center mt-5" >
                    <i className="fa fa-search h2"></i>
                    <input type="text" className="form-control w-75" placeholder="Search topics or keywords" value={this.props.query} onChange={this.handleChange}></input>
                    <button type="submit" className="btn btn-primary bg-dark" onClick={this.onSubmitHandler}>Search</button>
                </form>
            </div>
        );
    }
}
 
export default withRouter(SearchBox);
