import React, { Component } from 'react';
import PdfPreview from './PdfPreview'
class DocumentSection extends Component {
    componentDidMount(){
        if (this.props.name == "Recently Document"){
            for (let i=0; i < 4; i++){
                console.log("/RecentlyPDF/${i}");
                fetch("/RecentlyPDF/${i}")
                .then(res => res.blob())
                .then(
                    (result) =>{
                        var url = URL.createObjectURL(result);
                        console.log(url);
                        const canvas = this.refs.canvas;
                        const ctx = canvas.getContext("2d");
                        const img = new Image();
                        img.src = url;
                        img.onload = () =>{
                            ctx.drawImage(img,0,0,img.width,img.height,0,0,canvas.width,canvas.height);
                        }
                    }
                )
            }
        }
    }
    render() { 
        return (  
            <div className="d-block" style={{marginTop : '3%'}}>
                <p className="h4 mb-2 text-dark text-center border-bottom">{this.props.name}</p>
                <PdfPreview />
                <PdfPreview />
                <PdfPreview />
                <PdfPreview />
            </div>
        );
    }
}
const containerStyle = {
    paddingRight : '15%',
    paddingLeft : '15%'
};
const imageStyle = {
    marginTop : "5px",
    marginLeft : "3%",
    width : "13%",
    height : "40%"
};
export default DocumentSection
