import React, { Component } from 'react';
import RichTextEditor from '../Components/RichTextEditor'
import PdfContent from '../Components/PdfContent'
class ManualUploadPage extends Component {
    state = {
        editorStateText:null,
        uploadPdf: null
    }
    onEditorStateChange = (editorStateText) =>{
        this.setState({editorStateText});
    }

    fileChangeHandler = (event) => {
        this.setState({
            uploadFile: event.target.files[0],
            pageNumber: 1,
            numPages: null
        });
    }
    
    onClickUploadButton = (e) =>{
        //make post request to server
        const formData = new FormData();
        formData.append('pdfUpload', this.state.uploadPdf);
        fetch('/uploadDocument', {
            method: 'POST',
            body: formData
        })
        .catch(err => { console.log(err) } )

        e.preventDefault();
    }
    onClickSaveButton = (e) =>{
        var jsPDF = require('jspdf');
        var doc = new jsPDF();
        doc.text(this.state.editorStateText, 10, 10);
        doc.save('MyDocument.pdf');
    }
    render() { 
        return (  
            <div className="container">
                <div className="row">
                    <div className="col-6">
                        <RichTextEditor editorStateChangeHandler={this.onEditorStateChange}></RichTextEditor>
                        <form className="container text-center" onSubmit={this.onClickUploadButton}>
                            <button className="btn btn-info mt-2 col-6 w-25 mr-2" type="button" onClick={this.onClickSaveButton}>Save text</button>
                            <button className="btn btn-info mt-2 col-6 w-25" type="file" name="pdfUpload" onChange={this.fileChangeHandler}>Upload</button>
                        </form>
                        
                    </div>
                    <div className="col-6">
                        <PdfContent uploadFile = {this.props.location.state.uploadFile}></PdfContent>
                    </div>
                </div>
            </div>
        );
    }
}
export default ManualUploadPage;
