import React, { Component } from 'react';
import RichTextEditor from '../Components/RichTextEditor'
import PdfContent from '../Components/PdfContent'
class ManualUploadPage extends Component {
    state = {
        editorStateText:null,
        uploadPdf: null
    }
    onEditorStateChange = (editorStateText) =>{
        this.setState({editorStateText});
    }

    fileChangeHandler = (event) => {
        this.setState({
            uploadPdf: event.target.files[0]
        });
    }
    onClickSaveButton = (e) =>{
        var jsPDF = require('jspdf');
        var doc = new jsPDF();
        doc.text(this.state.editorStateText, 10, 10);
        //doc.save('MyDocument.pdf');
        
        var pdf = doc.output('blob');
        var formData = new FormData();
        formData.append('uploadFile', pdf);
        fetch('/manualUploadDocument', {
            method: 'POST',
            body: formData
        })
        .catch(err => { console.log(err) } )
        console.log(formData);
        //this.props.history.push('./upload_document')
    }
    render() { 
        return (  
            <div className="container">
                <div className="row">
                    <div className="col-6">
                        <RichTextEditor editorStateChangeHandler={this.onEditorStateChange}></RichTextEditor>
                        <div className="container text-center">
                            <button className="btn btn-info mt-2 col-6 w-25" type="button" onClick={this.onClickSaveButton}>Save text</button>
                        </div>
                        
                    </div>
                    <div className="col-6">
                        <PdfContent uploadFile = {this.props.location.state.uploadFile}></PdfContent>
                    </div>
                </div>
            </div>
        );
    }
}
export default ManualUploadPage;
