import React, { Component } from 'react';
class UpLoadDocumentPage extends Component {
    state = { 
        uploadFile:null
     }
    fileChangeHandler = (event) =>{
        console.log(event.target.files[0]);
        this.setState(event.target.files[0]);
    }
    uploadButton = () => {
        console.log(this.state.uploadFile)
        if (this.state.uploadFile)
            return(
                <button className="d-block btn btn-lg btn-light"></button>
            )
        else
            return <div className="d-block"></div>
    }
    render() { 
        return ( 
            <div className="container">
                <div className="row">
                    <div className="col-4 bg-light mt-5">
                        <input type="file" onChange={this.fileChangeHandler} className="d-block text-success" accept="application/pdf"></input>
                        {this.uploadButton()}
                    </div>
                    <div className="col-8 bg-primary">hi</div>
                </div>
            </div>
         );
    }
}
 
export default UpLoadDocumentPage;
