import React, { Component } from 'react';
import "./Signup.css";
class SignupPage extends Component {
    signup = (event) =>
    {
        event.preventDefault();
        let username = document.getElementById("username").value;
        let password = document.getElementById("password").value;
        let signupObj = {"username": username, "password": password};
        console.log(signupObj);
        
        fetch('/login', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(signupObj)         
        })
        .then(response => {
            console.log(response.status);
            if (response.status == 404){
                alert("Invalid username or password");
                event.preventDefault();
            }
            else{
                response.json()            
                .then(token => {
                    console.log(this.props);
                    localStorage.setItem('token',token);
                    this.props.onLoggedinChange(token);
                    this.props.history.push("/user_option");
                })
            }
        })
    }
    render() { 
        return ( 
            <div class="container">
                <div class="row">
                    <div class="col">
                        <h1 class="text-center">Signup</h1>
                        <div class="d-flex justify-content-center">
                            <div class="card mt-3">
                                <div class="card-body">
                                    <form>
                                        <div class="form-group">
                                            <label for="username">Username</label>
                                            <input type="text" id="username" class="form-control" name="username" placeholder="Enter username" required/>
                                        </div>
                                        <div class="form-group">
                                            <label for="username">Password</label>
                                            <input type="text" id="password" class="form-control" name="password" placeholder="Enter password" required/>
                                        </div>
                                        <input type="submit" class="btn btn-primary" value="Submit"/>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
         );
    }
} 
 
export default SignupPage;
