import React, { Component } from 'react';
class PdfPreviewCard extends Component {
    componentDidMount(){
        const canvas = this.refs.canvas;
        const ctx = canvas.getContext("2d");
        const img = new Image();
        img.onload = () =>{
            ctx.drawImage(img,canvas.width / 2 - img.width / 2,
                img.height / 2 - img.height / 2)
        }
    }
    render() { 
        return ( 
            <div className="card d-inline-block" style={{width: '18rem',marginLeft:'5%',marginTop:'2%'}}>
                <canvas ref="canvas" className="card-img-top" src={pdfImg} alt="Card image cap" />
                <div className="card-body">
                    <h5 className="card-title">Card title</h5>
                    <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    <a href="/" className="btn btn-primary">Go somewhere</a>
                </div>
            </div>
         );
    }
}
const pdfImg = require('../Resources/pdf.jpg');
export default PdfPreviewCard;
