import React, { Component } from 'react';
import './Heading.css'
import { Link } from 'react-router-dom'
var jwt = require("jsonwebtoken");

class Heading extends Component {
    
    constructor(props){
      super(props);
      var token = localStorage.getItem('token')
      if (!token)
        this.state = {
          isLoggedIn :false,
          username:null
        }
      else {

      }
    }
    setUserStatus = (token) =>{
      if (token){
        var decoded = jwt.verify(token,'shhhhh');
        this.setState({
          isLoggedIn:true,
          username:decoded.user
        })
      }
      else{
        this.setState({
          isLoggedIn:false,
          username:null
        })
      }
    }
    componentDidMount(){
      var token = localStorage.getItem('token')
      if (token){
        var decoded = jwt.verify(token,'shhhhh');
        this.setState({
          isLoggedIn:true,
          username:decoded.user
        })
      }
      else{
        this.setState({
          isLoggedIn:false,
          username:null
        })
      } 
    }
    render() { 
      return(
          <header className="border-bottom d-block">
            <div className="container">
              <nav className="navbar navbar-expand-sm navbar-light">
                <img src={teamLogoImg} className="navbar-brand"></img>
                <ul className="navbar-nav ml-auto">
                  <li className="nav-item"><Link to='/home' className="nav-link">Home</Link></li>
                  <li className="nav-item"><Link to='/document/all' className="nav-link">Document</Link></li>
                  <li className="nav-item"><Link to='/about-us' className="nav-link">About us</Link></li>
                  <li className="nav-item nav-link">|</li>
                  {
                    this.props.isLoggedIn ?
                    (
                      <div className="nav-item">
                        <li className="d-inline-block"><p>{this.props.username}</p></li>
                        <li className="d-inline-block" style={style.logoutBtn}><button className="btn default">Log out</button></li>
                     </div>
                    ) : (
                      <div className="nav-item">
                        <li className="d-inline-block"><Link to='/signup' className="nav-link">Sign up</Link></li>
                        <li className="d-inline-block"><Link to='/login' className="nav-link">Log in</Link></li>
                     </div>
                    )
                  }
                </ul>
              </nav>
            </div>  
          </header>
      );
    }
}
const style ={
  logoutBtn:{
    "border": "none",
    "background-color": "inherit"
  }
}
const teamLogoImg = require('../Resources/court-logo.png');
export default Heading;
