class AliasGenerator {
  constructor() {
    this.nextId = {};
    this.blocked = new Set();
  }

  next(prefix) {
    if (!(prefix in this.nextId)) {
      this.nextId[prefix] = 1;
    }

    let next;
    do {
      next = prefix + this.nextId[prefix]++;
    } while (this.blocked.has(next))

    return next
  }

  block(alias) {
    alias = String(alias);
    if (alias[0] === '/') {
      alias = alias.slice(1);
    }

    this.blocked.add(alias);
  }

  isBlocked(alias) {
    alias = String(alias);
    if (alias[0] === '/') {
      alias = alias.slice(1);
    }

    return this.blocked.has(alias)
  }

  reset(prefix) {
    this.nextId[prefix] = 1;
  }
}

